files.content:
:root {
    --primary-color: #8D6E63;
    --secondary-color: #D7CCC8;
    --accent-color: #FF7043;
    --text-color: #3E2723;
    --bg-color: #FAFAFA;
}

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background-color: var(--bg-color);
    color: var(--text-color);
    line-height: 1.6;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

header {
    background-color: var(--primary-color);
    color: white;
    padding: 1rem 0;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}

.header-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.cart-icon {
    font-weight: bold;
    background: rgba(255,255,255,0.2);
    padding: 0.5rem 1rem;
    border-radius: 20px;
}

.hero {
    text-align: center;
    padding: 3rem 0;
    background-color: var(--secondary-color);
    margin-bottom: 2rem;
    border-radius: 0 0 10px 10px;
}

.hero h2 {
    font-size: 2.5rem;
    margin-bottom: 0.5rem;
}

.cookie-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    margin-bottom: 3rem;
}

.cookie-card {
    background: white;
    border-radius: 10px;
    overflow: hidden;
    box-shadow: 0 4px 6px rgba(0,0,0,0.1);
    transition: transform 0.2s;
    display: flex;
    flex-direction: column;
}

.cookie-card:hover {
    transform: translateY(-5px);
}

.cookie-image {
    height: 200px;
    background-color: #eee;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 3rem;
}

.cookie-info {
    padding: 1.5rem;
    flex-grow: 1;
    display: flex;
    flex-direction: column;
}

.cookie-info h3 {
    margin-bottom: 0.5rem;
    color: var(--primary-color);
}

.price {
    font-size: 1.25rem;
    font-weight: bold;
    margin-bottom: 1rem;
    color: var(--accent-color);
}

button.add-btn {
    margin-top: auto;
    background-color: var(--primary-color);
    color: white;
    border: none;
    padding: 0.75rem;
    border-radius: 5px;
    cursor: pointer;
    font-size: 1rem;
    transition: background 0.3s;
}

button.add-btn:hover {
    background-color: #6D4C41;
}

footer {
    text-align: center;
    padding: 2rem;
    background-color: #3E2723;
    color: rgba(255,255,255,0.7);
}

const products = [
    { id: 1, name: "Classic Chocolate Chip", price: 2.50, emoji: "üç™" },
    { id: 2, name: "Oatmeal Raisin", price: 2.25, emoji: "üçá" },
    { id: 3, name: "Double Fudge Brownie", price: 3.00, emoji: "üç´" },
    { id: 4, name: "Macadamia Nut", price: 3.50, emoji: "ü•ú" },
    { id: 5, name: "Snickerdoodle", price: 2.00, emoji: "‚ú®" },
    { id: 6, name: "Red Velvet", price: 3.25, emoji: "üç∞" }
];

let cartCount = 0;

function init() {
    const productList = document.getElementById('product-list');

    products.forEach(product => {
        const card = document.createElement('div');
        card.className = 'cookie-card';

        card.innerHTML = `
            <div class="cookie-image">${product.emoji}</div>
            <div class="cookie-info">
                <h3>${product.name}</h3>
                <p class="price">$${product.price.toFixed(2)}</p>
                <button class="add-btn" onclick="addToCart('${product.name}')">Add to Cart</button>
            </div>
        `;

        productList.appendChild(card);
    });
}

function addToCart(productName) {
    cartCount++;
    document.getElementById('cart-count').innerText = cartCount;
    // Simple visual feedback
    const btn = event.target;
    const originalText = btn.innerText;
    btn.innerText = "Added!";
    btn.style.backgroundColor = "#4CAF50";

    setTimeout(() => {
        btn.innerText = originalText;
        btn.style.backgroundColor = "";
    }, 1000);

    console.log(`Added ${productName} to cart.`);
}

// Initialize the store when the page loads
document.addEventListener('DOMContentLoaded', init);

